#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n;
        int tot = 0;
        for(int i = 1; i <= n / 2; i++) {
            tot -= i;
        }
        for(int i = n; i > (n + 1) / 2; i--) {
            tot += i;
        }
        cout << tot + 1 << "\n";
    }
}
