#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n; int x;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> x;
        if(!x) {
            cout << (n == 1 ? -1 : (n % 2 ? n + 3 : n)) << "\n";
            continue;
        }
        if(__builtin_popcount(x) > 1) {
            int spots = min(n, __builtin_popcount(x));
            cout << x + n - spots + (spots % 2 == n % 2 ? 0 : 1) << "\n";
        } else {
            if(n == 2) {
                cout << (x == 1 ? 5 : x + 2) << "\n";
            } else {
                cout << (n % 2 ? x + n - 1 : (x == 1 ? n + 3 : x + n)) << "\n";
            }
        }
    }
}
