#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n; int k;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> k;
        vector<int> a(n);
        for(int i = 0; i < n; i++) {
            cin >> a[i];
        }
        sort(a.begin(), a.end());
        if(a[n - 1] - k - 1 > a[0]) {
            cout << "Jerry\n";
        } else if(a[n - 1] - k > a[0] && a[n - 1] == a[n - 2]) {
            cout << "Jerry\n";
        } else {
            ll sum = 0;
            for(int i : a) {
                sum += i;
            }
            cout << (sum % 2 ? "Tom" : "Jerry") << "\n";
        }
    }
}
