#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n; int m; int a; int b;

int f(int x) {
    if(x > 1) {
        return f(x / 2 + x % 2) + 1;
    }
    return 0;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> m >> a >> b;
        cout << min({f(a) + f(m), f(n - a + 1) + f(m), f(n) + f(b), f(n) + f(m - b + 1)}) + 1 << "\n";
        
    }
}
