#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

string s;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> s;
        int n = stoi(s);
        int v = sqrt(n);
        bool found = false;
        for(int i = v - 2; i <= v + 2; i++) {
            if(i >= 0 && i * i == n) {
                found = true;
                cout << "0 " << i << "\n";
                break;
            }
        }
        if(!found) {
            cout << "-1\n";
        }
    }
}
