#include <bits/stdc++.h>
#include <cmath>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n; int k;
string s;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> k >> s;
        k = n / 2 - k;
        int ones = count(s.begin(), s.end(), '1');
        int zeros = n - ones;
        cout << ((min(ones, zeros) >= k && !((ones - k) % 2)) ? "YES" : "NO") << "\n";
    }
}
