#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n;
        vector<int> a(n);
        for(int i = 0; i < n; i++) {
            cin >> a[i];
        }
        map<int, int> mp;
        int mx = 0;
        for(int i = n - 1; i >= 0; i--) {
            map<int, int>::iterator it = mp.lower_bound(a[i] + 2);
            if(it == mp.end()) {
                mp[a[i]] = 1;
            } else {
                mp[a[i]] = it->second + 1;
            }
            mx = max(mx, mp[a[i]]);
            mp[a[i]] = max(mp[a[i]], mx);
        }
        cout << mp[a[0]] << "\n";
    }
}
