#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n; int m;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> m;
        vector<array<int, 2>> ab(n);
        for(int i = 0; i < n; i++) {
            cin >> ab[i][0] >> ab[i][1];
        }
        int wochien = 0;
        int cur = 0; int side = 0;
        for(array<int, 2> i : ab) {
            int totTime = i[0] - cur;
            if(i[1] == side) {
                wochien += totTime - totTime % 2;
            } else {
                wochien += totTime - (1 - (totTime % 2));
            }
            cur = i[0]; side = i[1];
        }
        wochien += m - ab[n - 1][0];
        cout << wochien << "\n";
    }
}
