#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const int MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n; int rk; int ck; int rd; int cd;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> rk >> ck >> rd >> cd;
        int dr = abs(rk - rd);
        int dc = abs(ck - cd);
        int runR; int runC;
        if(rk < rd) {
            runR = rk; 
        } else if(rk > rd) {
            runR = n - rk;
        } else {
            runR = 1;
        }
        if(ck < cd) {
            runC = ck;
        } else if(ck > cd) {
            runC = n - ck;
        } else {
            runC = 1;
        }
        cout << max(runR + dr, runC + dc) << "\n";
    }
}
