#include <bits/stdc++.h>
#include <cstdio>

using namespace std;

using ll = long long;
const int MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n;
        vector<int> A(n);
        for(int i = 0; i < n; i++) {
            cin >> A[i];
        }
        vector<int> freq(167);
        for(int i : A) {
            freq[i]++;
        }
        int wochien = 0;
        while(freq[wochien]) {
            wochien++;
        }
        cout << wochien << "\n";
    }
}
