#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const int MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n;
        vector<ll> b(n);
        for(int i = 0; i < n; i++) {
            cin >> b[i];
        }
        int cur = 0;
        vector<int> a(n);
        ll prv = 0;
        for(int i = 0; i < n; i++) {
            int same = b[i] - (i ? b[i - 1] : 0LL) - 1;
            if(same == i) {
                a[i] = ++cur;
            } else {
                a[i] = a[i - same - 1];
            }
        }
        for(int i : a) {
            cout << i << " ";
        }
        cout << '\n';
    }
}
