#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n;
        vector<int> a(n);
        for(int i = 0; i < n; i++) {
            cin >> a[i];
        }
        vector<int> prefMax(n);
        prefMax[0] = a[0];
        for(int i = 1; i < n; i++) {
            prefMax[i] = max(prefMax[i - 1], a[i]);
        }
        ll sum = 0;
        for(int i = n - 1; i >= 0; i--) {
            sum += a[i];
            if(!i) {
                cout << sum << " ";
            } else {
                cout << (prefMax[i - 1] > a[i] ? sum - a[i] + prefMax[i - 1] : sum) << " ";
            }
        }
        cout << "\n";
    }
}
