#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const ll MOD = 1E9 + 7;
const int INF = 1E9; const ll INFLL = 1E18;

int n;
string s;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n >> s;
        if(s[n - 1] == 'B') {
            cout << (count(s.begin(), s.end(), 'B') == 1 ? "Alice" : "Bob") << "\n";
        } else {
            cout << (s[0] == 'B' ? (n == 1 || s[n - 2] == 'B' ? "Bob" : "Alice") : "Alice") << "\n";
        }
    }
}
