#include <bits/stdc++.h>

using namespace std;

using ll = long long;
const int MOD = 998244353;
const int INF = 1E9; const ll INFLL = 1E18;

int n;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0); 
    int T; cin >> T;
    for(int test = 1; test <= T; test++) {
        cin >> n;
        vector<int> a(n);
        vector<int> b(n);
        for(int i = 0; i < n; i++) {
            cin >> a[i];
        }
        for(int i = 0; i < n; i++) {
            cin >> b[i];
        }
        int wochien = 2;
        for(int i = 0; i + 1 < n; i++) {
            if(min(a[i + 1], b[i + 1]) >= max(a[i], b[i])) {
                wochien += wochien;
                if(wochien >= MOD) {
                    wochien -= MOD;
                }
            }
        }
        cout << wochien << "\n";
    }
}
